  // word comparison by J.Zou
  public static void wordCheck(char guess, String wordToGuess) {
    String display = "";
    char i = 0;
    int length = wordToGuess.length();
    while (i < length) {
      if (save.charAt(i) != '_') {
        display += save.charAt(i);
        i++;
      } else if (guess == wordToGuess.charAt(i)) {
        display += guess;
        i++;
      } else if (wordToGuess.charAt(i) == ' ') {
        display += " ";
        i++;
      } else {
        display += "_";
        i++;
      }
    }
    save = display;
    System.out.println(save);

    //win logic done by J.Zou and E.Luo
    if (save.equals(wordToGuess)) {
      winScreen(lives);
      System.out.println("play again?(y/n)");
      if (scan.nextLine().charAt(0) == 'y') {
        win++;
        System.out.println("round " + round + "...FIGHT! WLR " + win + ":" + loss);
        lives = 9;
        k++;
      } else{
        System.out.println("See you next time!");
        help++;
        k++;
      }
    }
  }

